const stack = document.querySelector('.drag-stack');
let cards = [...document.querySelectorAll('.card')];

let startX = 0;
let currentX = 0;
let draggingCard = null;

function setCardTransforms() {
  cards.forEach((card, index) => {
    const offsetY = -30 * index;
    const offsetZ = -40 * index;
    card.style.transform = `translateX(0px) translateY(${offsetY}px) translateZ(${offsetZ}px) rotateY(0deg)`;
    card.style.transition = 'transform 0.3s ease';
    card.classList.remove('dragging');
  });
}

function onPointerDown(e) {
  if (cards.length === 0) return;
  draggingCard = cards[0];
  startX = e.clientX || (e.touches && e.touches[0].clientX);
  draggingCard.style.transition = 'none';
  draggingCard.classList.add('dragging');
  stack.style.userSelect = 'none';
}

function onPointerMove(e) {
  if (!draggingCard) return;
  currentX = e.clientX || (e.touches && e.touches[0].clientX);
  const deltaX = currentX - startX;
  const rotateY = deltaX * 0.3;
  draggingCard.style.transform = `translateX(${deltaX}px) translateY(calc(var(--i) * -30px)) translateZ(calc(var(--i) * -40px)) rotateY(${rotateY}deg)`;
}

function onPointerUp(e) {
  if (!draggingCard) return;
  const deltaX = currentX - startX;
  const threshold = 120;

  if (Math.abs(deltaX) > threshold) {
    draggingCard.style.transition = 'transform 0.4s ease, opacity 0.4s ease';
    draggingCard.style.opacity = '0';
    draggingCard.style.transform = `translateX(${deltaX > 0 ? 500 : -500}px) translateY(calc(var(--i) * -30px)) translateZ(calc(var(--i) * -40px)) rotateY(${deltaX > 0 ? 45 : -45}deg)`;
    setTimeout(() => {
      if (draggingCard && draggingCard.parentNode === stack) {
        stack.removeChild(draggingCard);
      }
      cards = [...document.querySelectorAll('.card')];
      updateCardIndexes();
      setCardTransforms();
    }, 400);
  } else {
    draggingCard.style.transition = 'transform 0.4s ease';
    draggingCard.style.transform = `translateX(0) translateY(calc(var(--i) * -30px)) translateZ(calc(var(--i) * -40px)) rotateY(0deg)`;
  }
  draggingCard.classList.remove('dragging');
  draggingCard = null;
  stack.style.userSelect = '';
}

function updateCardIndexes() {
  cards.forEach((card, idx) => {
    card.style.setProperty('--i', idx);
    card.style.zIndex = 100 - idx;
  });
}

stack.addEventListener('mousedown', onPointerDown);
stack.addEventListener('touchstart', onPointerDown);
window.addEventListener('mousemove', onPointerMove);
window.addEventListener('touchmove', onPointerMove);
window.addEventListener('mouseup', onPointerUp);
window.addEventListener('touchend', onPointerUp);

updateCardIndexes();
setCardTransforms();

