const sphere = document.querySelector('.gradient-sphere-container');
const sphereInner = document.querySelector('.gradient-sphere');

let isDragging = false;
let startX;
let currentRotationY = 0;

sphere.addEventListener('mousedown', (e) => {
  isDragging = true;
  startX = e.clientX;
  sphere.style.cursor = 'grabbing';
});

window.addEventListener('mouseup', () => {
  isDragging = false;
  sphere.style.cursor = 'grab';
});

window.addEventListener('mousemove', (e) => {
  if (!isDragging) return;

  const deltaX = e.clientX - startX;
  startX = e.clientX;

  currentRotationY += deltaX * 0.5;
  sphereInner.style.transform = `rotateY(${currentRotationY}deg)`;
});

