const canvas = document.getElementById('slashCanvas');
const ctx = canvas.getContext('2d');
let width, height;

function resize() {
  width = window.innerWidth;
  height = window.innerHeight;
  const dpr = window.devicePixelRatio || 1;
  canvas.width = width * dpr;
  canvas.height = height * dpr;
  canvas.style.width = width + 'px';
  canvas.style.height = height + 'px';
  ctx.setTransform(1, 0, 0, 1, 0, 0);
  ctx.scale(dpr, dpr);
}
resize();
window.addEventListener('resize', resize);

class Slash {
  constructor(x, y, dx, dy) {
    this.x = x;
    this.y = y;
    this.dx = dx * 5;
    this.dy = dy * 5;
    this.alpha = 1;
    this.length = 100;
    this.life = 20;
    this.age = 0;
  }
  update() {
    this.x += this.dx;
    this.y += this.dy;
    this.age++;
    this.alpha = 1 - this.age / this.life;
  }
  draw() {
    ctx.save();
    ctx.lineCap = 'round';
    ctx.strokeStyle = `rgba(255, 230, 130, ${this.alpha})`;
    ctx.shadowColor = `rgba(255, 230, 130, ${this.alpha})`;
    ctx.shadowBlur = 20;
    ctx.lineWidth = 6 * this.alpha;
    ctx.beginPath();
    ctx.moveTo(this.x, this.y);
    ctx.lineTo(this.x - this.dx * this.length, this.y - this.dy * this.length);
    ctx.stroke();
    ctx.restore();
  }
}

const slashes = [];

function animate() {
  ctx.clearRect(0, 0, width, height);
  for (let i = slashes.length -1; i >= 0; i--) {
    const s = slashes[i];
    s.update();
    s.draw();
    if (s.age >= s.life) slashes.splice(i,1);
  }
  requestAnimationFrame(animate);
}
animate();

let lastX = 0;
let lastY = 0;
let isDragging = false;

const sword = document.getElementById('sword');
sword.addEventListener('pointerdown', e => {
  isDragging = true;
  lastX = e.clientX;
  lastY = e.clientY;
});
window.addEventListener('pointerup', e => {
  isDragging = false;
});
window.addEventListener('pointermove', e => {
  if (!isDragging) return;
  const dx = e.clientX - lastX;
  const dy = e.clientY - lastY;
  if (Math.abs(dx) > 3 || Math.abs(dy) > 3) {
    slashes.push(new Slash(e.clientX, e.clientY, dx, dy));
    lastX = e.clientX;
    lastY = e.clientY;
  }
});

